INSERT INTO wrc.wrc_airroute_flow_detail (gmt_created, gmt_modified, created_by, modified_by, id, flow_name, flow_context, flow_order, airroute_id, include_food, include_rest, album_id) VALUES ('2017-08-25 17:50:39', '2017-08-25 17:50:43', 'TEST', 'TEST', '1', '第一天', '<p>上海出发乘指定航班飞往普吉岛, 入住普吉岛豪华别墅休息调整,别墅厨师亲自烹饪泰式晚餐享用.</p>', 0, '1', '含晚餐', '豪华别墅', null);
INSERT INTO wrc.wrc_airroute_flow_detail (gmt_created, gmt_modified, created_by, modified_by, id, flow_name, flow_context, flow_order, airroute_id, include_food, include_rest, album_id) VALUES ('2017-08-25 18:13:22', '2017-08-25 18:13:25', 'TEST', 'TEST', '2', '第二天', '<p>10:00 别墅出发前往码头乘船1小时到达原始瑜伽岛.</p><p>12:30 入住房间,熟悉瑜伽岛环境,午餐,与下午</p><p>14:30开始第一堂课的练习</p><p>16:30-18:30,晚餐馆内享用</p>', 0, '1', '含三餐', '瑜伽岛', null);
